      this.result.artists = response;
    }, err => {
      console.log(err);
    }).add(() => {
      this.loading.artists = false;
    });
  }

  fetchAlbums() {
    this.loading.albums = true;
    this.httpService.get(`/albums/random?total=20`).subscribe(response => {
      this.result.albums = response;
    }, err => {
      console.log(err);
    }).add(() => {
      this.loading.albums = false;
    });
  }

  fetchTracks() {
    this.loading.tracks = true;
    this.httpService.get(`/tracks/random?total=20`).subscribe(response => {
      this.result.tracks = response;
    }, err => {
      console.log(err);
    }).add(() => {
      this.loading.tracks = false;
    });
  }

  fetchPlaylists() {
    this.loading.playlists = true;
    this.httpService.get(`/playlists`).subscribe(response => {
      this.playlists = [{
        name: "Favorites",
        id: "FAVOURITES"
      }].concat(response.playlists);
